/* A Naive recursive implementation of LCS problem */
#include<stdio.h>
#include<stdlib.h>

#define MAX 1000010
int dp[MAX][6];
int max(int a, int b);
 
/* Returns length of LCS for X[0..m-1], Y[0..n-1] */
int lcs( char *X, char *Y, int m, int n )
{
   if (m == 0 || n == 0)
     return 0;
    
    
   if (X[m-1] != Y[n-1])
     return lcs(X, Y, m-1, n);
   else
   {
   //	printf("%c\n",X[m]);
     return 1+(max(lcs(X, Y, m-1, n-1), lcs(X, Y, m-1, n)));
}
}
 
 
 int lcs_dp( char *X, char *Y, int k, int l )
{
   int m,n;
   for(m=0;m<=k;m++)
   {
   	for(n=0;n<=l;n++)
   	{
   	if (m == 0 || n == 0)
     		dp[m][n]=0;
    else if (X[m-1] != Y[n-1])
     	dp[m][n]=dp[m-1][n];
   else
       dp[m][n]=1+(max(dp[m-1][n-1],dp[m-1][n]));
       
    
   	}
   	
   }

for(m=0;m<=k;m++)
   {
   	for(n=0;n<=l;n++)
   	{

       printf("m = %d , n = %d , Val = %d\n",m,n,dp[m][n]);
       
}
   	}
return dp[k][l];
}
 
